import React, { useState } from "react";

const UseStateHook = () => {
  const [count, setCount] = useState(0);

  const handleInc = () => {
    setCount(count + 1);
  };
  const handleClr = () => {
    setCount(0);
  };
  const handleDec = () => {
    if (count > 0) {
      setCount(count - 1);
    } else {
      alert(`Count değeri negatif değer olamaz!`);
    }
  };

  return (
    <div className="container">
      <h2 className="text-danger">Functional Components</h2>
      <h3 className="display-4">UseState Counter: {count}</h3>
      <button onClick={handleInc} className="btn btn-success">
        INC
      </button>
      <button onClick={handleClr} className="btn btn-warning">
        CLR
      </button>
      <button onClick={handleDec} className="btn btn-danger">
        DEC
      </button>
    </div>
  );
};

export default UseStateHook;
